import React from 'react';
import '../styles/RecommendationsModal.css';

const RecommendationsModal = ({ recommendations, loading, error, onClose, onSave, isSaving, teamName, teamDescription, onTeamNameChange, onTeamDescriptionChange }) => {
  if (!recommendations && !loading && !error) {
    return null;
  }

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        {/* Header */}
        <div className="modal-header">
          <h2>üéØ AI-Generated Team Recommendations</h2>
          <button className="modal-close" onClick={onClose}>‚úï</button>
        </div>

        {/* Loading State */}
        {loading && (
          <div className="modal-body">
            <div className="loading-state">‚ú® Generating recommendations...</div>
          </div>
        )}

        {/* Error State */}
        {error && (
          <div className="modal-body">
            <div className="error-state">‚ùå {error}</div>
          </div>
        )}

        {/* Recommendations Content */}
        {recommendations && !loading && (
          <div className="modal-body">
            {/* Suggested Pokemon */}
            {recommendations.suggestedPokemon && recommendations.suggestedPokemon.length > 0 && (
              <div className="recommendation-section">
                <h3>‚ûï Suggested Pokemon to Add</h3>
                <div className="suggested-pokemon-list">
                  {recommendations.suggestedPokemon.map((pokemon, index) => (
                    <div key={index} className="suggested-pokemon-tag">
                      {pokemon}
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Team Composition */}
            {recommendations.teamComposition && recommendations.teamComposition.length > 0 && (
              <div className="recommendation-section">
                <h3>üë• Detailed Team Composition</h3>
                <div className="composition-grid">
                  {recommendations.teamComposition.map((member, index) => (
                    <div key={index} className="team-member-card">
                      <div className="member-header">
                        <h4>{member.name}</h4>
                        {member.role && <span className="member-role">{member.role}</span>}
                      </div>

                      <div className="member-info">
                        {member.moves && (
                          <div className="info-section">
                            <strong>üéÆ Moves:</strong>
                            <ul>
                              {member.moves.map((move, i) => (
                                <li key={i}>{move}</li>
                              ))}
                            </ul>
                          </div>
                        )}

                        {member.ability && (
                          <div className="info-section">
                            <strong>‚ú® Ability:</strong>
                            <p>{member.ability}</p>
                          </div>
                        )}

                        {member.item && (
                          <div className="info-section">
                            <strong>üéÅ Held Item:</strong>
                            <p>{member.item}</p>
                          </div>
                        )}

                        {member.evs && (
                          <div className="info-section">
                            <strong>üìä EVs:</strong>
                            <p>{member.evs}</p>
                          </div>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Strategy Analysis */}
            {recommendations.strategyAnalysis && (
              <div className="recommendation-section">
                <h3>üéØ Strategy Analysis</h3>
                <p className="strategy-text">{recommendations.strategyAnalysis}</p>
              </div>
            )}

            {/* Save Section */}
            <div className="modal-save-section">
              <h3>üíæ Save This Team</h3>
              <div className="save-form">
                <input
                  type="text"
                  value={teamName}
                  onChange={(e) => onTeamNameChange(e.target.value)}
                  placeholder="Enter team name"
                  className="save-input"
                />
                <textarea
                  value={teamDescription}
                  onChange={(e) => onTeamDescriptionChange(e.target.value)}
                  placeholder="Team description (optional)"
                  className="save-textarea"
                />
                <button
                  onClick={onSave}
                  disabled={isSaving || !teamName.trim()}
                  className="save-team-btn"
                >
                  {isSaving ? 'Saving...' : 'Save Team'}
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default RecommendationsModal;
