import React, { useState, useEffect } from 'react';
import PokemonPicker from './components/PokemonPicker';
import TeamBuilder from './components/TeamBuilder';
import RecommendationsModal from './components/RecommendationsModal';
import Login from './components/Login';
import SignUp from './components/SignUp';
import NavBar from './components/NavBar';
import TeamsPage from './components/TeamsPage';
import { teamAPI } from './api';
import { onAuthChange, logout } from './services/authService';
import { saveTeam } from './services/teamService';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [authMode, setAuthMode] = useState('login'); // 'login', 'signup'
  const [currentPage, setCurrentPage] = useState('home'); // 'home', 'teams'
  const [selectedPokemon, setSelectedPokemon] = useState([]);
  const [strategy, setStrategy] = useState('');
  const [recommendations, setRecommendations] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [teamName, setTeamName] = useState('');
  const [teamDescription, setTeamDescription] = useState('');

  // Listen for auth state changes
  useEffect(() => {
    const unsubscribe = onAuthChange((currentUser) => {
      setUser(currentUser);
    });

    return unsubscribe;
  }, []);

  const handlePokemonSelect = (pokemon) => {
    setSelectedPokemon(pokemon);
  };

  const handleStrategyChange = (newStrategy) => {
    setStrategy(newStrategy);
  };

  const handleGenerateTeam = async (teamStrategy) => {
    try {
      setLoading(true);
      setError(null);

      // Get Pokemon names
      const pokemonNames = selectedPokemon.map(p => p.name);

      // Call API to generate team
      const response = await teamAPI.generateTeam(pokemonNames, teamStrategy);

      if (response.data.success) {
        setRecommendations(response.data.recommendations);
        setTeamName('');
        setTeamDescription('');
      } else {
        setError(response.data.error || 'Failed to generate team');
      }
    } catch (err) {
      console.error('Error generating team:', err);
      if (err.response?.status === 429) {
        setError('Rate limit exceeded. Please wait before trying again.');
      } else {
        setError(err.message || 'Failed to generate team recommendations');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleSaveTeam = async () => {
    if (!user) {
      setError('Please log in to save teams');
      return;
    }

    if (!teamName.trim()) {
      setError('Please enter a team name');
      return;
    }

    try {
      setLoading(true);
      setError(null);

      await saveTeam(user.uid, {
        name: teamName,
        description: teamDescription,
        pokemon: selectedPokemon,
        strategy: strategy,
        recommendations: recommendations
      });

      setError(null);
      alert('Team saved successfully!');
      setTeamName('');
      setTeamDescription('');
    } catch (err) {
      setError(err.message || 'Failed to save team');
    } finally {
      setLoading(false);
    }
  };

  const handleLoadTeam = (team) => {
    setSelectedPokemon(team.pokemon || []);
    setStrategy(team.strategy || '');
    setRecommendations(team.recommendations || null);
    setTeamName(team.name);
    setTeamDescription(team.description || '');
    window.scrollTo(0, 0);
  };

  const handleLogout = async () => {
    try {
      await logout();
      setSelectedPokemon([]);
      setStrategy('');
      setRecommendations(null);
      setTeamName('');
      setTeamDescription('');
    } catch (err) {
      setError(err.message || 'Failed to logout');
    }
  };

return (
    <div className="app">
      <header className="app-header">
        <div className="header-content">
          <img src={pokeballIcon} alt="PokÃ©ball" className="header-pokeball" />
          <h1>PokÃ©mon Team Creator</h1>
          <p>Build your perfect team with AI assistance</p>
          {user && (
            <div className="user-info">
              <span>Welcome, {user.displayName || user.email}</span>
              <button onClick={handleLogout} className="logout-btn">Logout</button>
            </div>
          )}
        </div>
      </header>

      <main className="app-main">
        {!user ? (
          <>
            {authMode === 'login' ? (
              <Login
                onLoginSuccess={() => setAuthMode('login')}
                onSwitchToSignup={() => setAuthMode('signup')}
              />
            ) : (
              <SignUp
                onSignupSuccess={() => setAuthMode('login')}
                onSwitchToLogin={() => setAuthMode('login')}
              />
            )}
          </>
        ) : (
          <div className="app-container">
            <div className="left-panel">
              <PokemonPicker
                selectedPokemon={selectedPokemon}
                onSelect={handlePokemonSelect}
                maxTeamSize={6}
              />
            </div>

            <div className="right-panel">
              <TeamBuilder
                selectedPokemon={selectedPokemon}
                onStrategyChange={handleStrategyChange}
                onGenerateTeam={handleGenerateTeam}
                loading={loading}
              />

              {recommendations && (
                <div className="save-team-section">
                  <h3>ðŸ’¾ Save This Team</h3>
                  <div className="save-team-form">
                    <input
                      type="text"
                      value={teamName}
                      onChange={(e) => setTeamName(e.target.value)}
                      placeholder="Team name"
                      className="save-input"
                    />
                    <textarea
                      value={teamDescription}
                      onChange={(e) => setTeamDescription(e.target.value)}
                      placeholder="Team description (optional)"
                      className="save-textarea"
                    />
                    <button
                      onClick={handleSaveTeam}
                      disabled={loading || !teamName.trim()}
                      className="save-team-btn"
                    >
                      Save Team
                    </button>
                  </div>
                </div>
              )}

              {error && <div className="error-banner">{error}</div>}

              <Recommendations
                recommendations={recommendations}
                loading={loading}
                error={error}
              />

              {user && <SavedTeams userId={user.uid} onLoadTeam={handleLoadTeam} />}
            </div>
          </div>
        )}
      </main>

      {user && (
        <footer className="app-footer">
          <p>Pokemon Team Creator - Powered by AI | Logged in as {user.email}</p>
        </footer>
      )}
    </div>
  );
}

export default App;
