import React, { useState } from 'react';
import { logout } from '../services/authService';
import '../styles/NavBar.css';
import pokeballIcon from '../pokeball.png';

const NavBar = ({ user, onLogout, onNavigate, currentPage }) => {
  const [showTeamsDropdown, setShowTeamsDropdown] = useState(false);

  const handleLogout = async () => {
    try {
      await logout();
      onLogout();
    } catch (err) {
      console.error('Logout failed:', err);
    }
  };

  const handleNavigate = (page) => {
    onNavigate(page);
    setShowTeamsDropdown(false);
  };

  return (
    <nav className="navbar">
      <div className="navbar-content">
        {/* Logo/Home */}
        <div className="navbar-brand" onClick={() => handleNavigate('home')}>
          <img src={pokeballIcon} alt="PokÃ©ball" className="navbar-pokeball" />
          <span>PokÃ©AI</span>
        </div>

        {/* Navigation Links */}
        <div className="navbar-nav">
          <button
            className={`nav-link ${currentPage === 'home' ? 'active' : ''}`}
            onClick={() => handleNavigate('home')}
          >
            Build Team
          </button>
          <button
            className={`nav-link ${currentPage === 'teams' ? 'active' : ''}`}
            onClick={() => handleNavigate('teams')}
          >
            ðŸ“š Saved Teams
          </button>
        </div>

        {/* User Info and Logout */}
        <div className="navbar-user">
          <span className="user-email">{user.displayName || user.email}</span>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>
    </nav>
  );
};

export default NavBar;
